   323â†’	// Prosaic dimensions - influencing (declared state shapes response)
   324â†’	if (ctx.prosaic) {
   325â†’		if (ctx.prosaic.urgency && ctx.prosaic.urgency > 0) influencing.push('âš¡ urgency');
   326â†’		if (ctx.prosaic.health && ctx.prosaic.health > 0) influencing.push('ðŸ’Š health');
   327â†’		if (ctx.prosaic.cognitive && ctx.prosaic.cognitive > 0) influencing.push('ðŸ§© cognitive');
   328â†’		if (ctx.prosaic.affect && ctx.prosaic.affect > 0) influencing.push('ðŸ’­ affect');
   329â†’	}
   330â†’
   331â†’	return { transmitted, withheld, influencing };
   332â†’}
