import { json } from '@sveltejs/kit';
import type { RequestHandler } from './$types';

export const POST: RequestHandler = async ({ request }) => {
	const { query, vcp_context, constitution_id } = await request.json();

	const apiKey = import.meta.env.VITE_ANTHROPIC_API_KEY ?? '';

	if (!apiKey) {
		return json({ fallback: true, reason: 'no_api_key' });
	}

	const systemPrompt = buildSystemPrompt(vcp_context, constitution_id);

	const response = await fetch('https://api.anthropic.com/v1/messages', {
		method: 'POST',
		headers: {
			'Content-Type': 'application/json',
			'x-api-key': apiKey,
			'anthropic-version': '2023-06-01'
		},
		body: JSON.stringify({
			model: 'claude-sonnet-4-5-20250929',
			max_tokens: 1024,
			system: systemPrompt,
			messages: [{ role: 'user', content: query }],
			stream: true
		})
	});

	if (!response.ok) {
		return json({
			fallback: true,
			reason: 'api_error',
			message: `API returned ${response.status}`
		});
	}

	return new Response(response.body, {
		headers: {
			'Content-Type': 'text/event-stream',
			'Cache-Control': 'no-cache',
			Connection: 'keep-alive'
		}
	});
};

function buildSystemPrompt(
	vcpContext: Record<string, unknown> | undefined,
	constitutionId: string
): string {
	// Strip private_context -- NEVER transmit
	const safeContext: Record<string, unknown> = {};
	if (vcpContext) {
		for (const [key, value] of Object.entries(vcpContext)) {
			if (key !== 'private_context') {
				safeContext[key] = value;
			}
		}
	}

	return `You are an AI advisor using the Steward persona from the VCP (Value Context Protocol).
Your constitution: ${constitutionId}

Your role: Help the user navigate obligation, guilt, and responsibility with calm, structured empathy.

Key rules:
1. Help distinguish genuine obligation from guilt-driven over-giving
2. Reframe boundary-setting as responsible stewardship, not selfishness
3. Consider long-term patterns and precedents
4. Balance needs across all affected parties including the user
5. Ensure decisions are repeatable and sustainable

User's current state: ${JSON.stringify(safeContext.personal_state ?? {})}
User's constraints: ${JSON.stringify(safeContext.constraints ?? {})}

Be warm but structured. Ask clarifying questions. Never be preachy.`;
}

